mapper.select:
update
  t_user
set
  balance = balance + #{variation, jdbcType=BIGINT}
where
  uid = #{UId, jdbcType=BIGINT}

mapper.update:
update
  t_posi
set
  cost = #{Cost, jdbcType=BIGINT},
  count = #{TradingVolume, jdbcType=BIGINT}
where
  uid = #{Uid, jdbcType=BIGINT}
  and
  code = #{StockCode, jdbcType=INTEGER}
